import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
function Tile(props) {
    const produceResource = () => {
        console.log("I produced resources of" + props.type);
    };
    if (props.isSand) {
        return (
            <button className="square sand">
            </button>
        );
    } else {
        return (
            <button className={`square ${props.type}`}
            onClick={produceResource}>
                {props.diceValue}
            </button>
        );
    }

}

function SeaBorder(props) {
    return (
        <div className={`seaBorder ${props.orientation}`}>
            3:1 Sheep Port
        </div>
    )
}

class Board extends React.Component {
    renderSquare() {
        let type = getType();
        let number = Math.ceil(Math.random() * 12);
        return (
            <Tile
                diceValue={number}
                type={type}
            />
        );
    }
    renderSand() {
        return (
            <Tile/>
        );
    }
    render() {
        return (
            <div>
                {this.renderSeaBorder("top-left")}
                {this.renderSeaBorder("top")}
                {this.renderSeaBorder("top-right")}
                {this.renderSeaBorder("left")}


                <div className="hexagons">
                    <div className="top-row">
                        {this.renderSquare()}
                        {this.renderSquare()}
                        {this.renderSquare()}
                    </div>
                    <div className="top-middle">
                        {this.renderSquare()}
                        {this.renderSquare()}
                        {this.renderSquare()}
                        {this.renderSquare()}
                    </div>
                    <div className="middle">
                        {this.renderSquare()}
                        {this.renderSquare()}
                        {this.renderSand()}
                        {this.renderSquare()}
                        {this.renderSquare()}
                    </div>

                    <div className="bottom-middle">
                        {this.renderSquare(6)}
                        {this.renderSquare(7)}
                        {this.renderSquare(8)}
                        {this.renderSquare(8)}
                    </div>

                    <div className="bottom-row">
                        {this.renderSquare(6)}
                        {this.renderSquare(7)}
                        {this.renderSquare(8)}
                    </div>
                </div>
                {this.renderSeaBorder("right")}
                {this.renderSeaBorder("bottom-left")}
                {this.renderSeaBorder("bottom")}
                {this.renderSeaBorder("bottom-right")}
            </div>
        );
    }

    renderSeaBorder(orientation) {
        return (
            <SeaBorder
                orientation={orientation}
                tradingPosts={"3:1 Sheep"}
            />
        );
    }

}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            history: [{
                squares: Array(9).fill(null),
            }],
            stepNumber: 0,
            xIsNext: true,
        };
    }
    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (calculateWinner(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? "X" : "O";
        this.setState({
            history: history.concat([{
                    squares: squares
            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext
        });
    }
    jumpTo(step) {
        this.setState({
            stepNumber: step,
            xIsNext: (step % 2) === 0,
        });
    }
    render() {
        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = calculateWinner(current.squares);
        const moves = history.map((step, move) => {
            const desc = move ?
                'Go to move #' + move :
                'Go to game start';
            return (
                <li key={move}>
                    <button onClick={() => this.jumpTo(move)}>{desc}</button>
                </li>
            );
        });

        let status;
        if (winner) {
            status = 'Winner: ' + winner;
        } else {
            status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

        return (
            <div className="game">
                <div className="game-board">
                    <Board
                        squares={current.squares}
                        onClick={(i) => this.handleClick(i)}
                    />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                    <ol>{moves}</ol>
                </div>
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);

function getType() {
    const types = ["grass", "stone", "brick", "hay", "sheep"];
    return types[Math.floor(Math.random() * 5)];
}

function calculateWinner(squares) {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
        }
    }
    return null;
}
